function getSliderItem(t,e){return'<li><a class="product-link" target="_blank" href="./detail.html?file='+t.title+'&_multiscreen=1"><h6 class="product-title">'+(e+1)+"."+t.title+(t.author?"("+t.author+")":"")+" </h6><p> "+t.desc+"</p></a> </li>"}$(document).ready(function(){$.ajax("./js/menu.json").done(function(t){for(var e=$(".home-product-content"),l=[],n=0;n<t.length-1;n+=2){var u=getSliderItem(t[n],n),o=getSliderItem(t[n+1],n+1);l.push('<ul class="product-column-two">'+u+o+"</ul>")}t.length%2==1&&l.push('<ul class="product-column-one">'+getSliderItem(t[t.length-1],t.length-1)+"</ul>"),e.append(l.join(""))})});